  and college_id = #{collegeId,jdbcType=INTEGER}

mapper.insert:
insert into college_tag (tag_id, college_id, create_time
  )
values (#{tagId,jdbcType=INTEGER}, #{collegeId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
  )

